import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronUp, faChevronDown } from '@fortawesome/free-solid-svg-icons';
import styled from 'styled-components';

function ChevronUpDown() {
  const [isVisible, setIsVisible] = useState(true);
  const Text = "Les annonces postées sur Kasa garantissent une fiabilité totale. Les photos sont conformes aux logements, et toutes les informations sont régulièrement vérifiées par nos équipes.";

  const handleClick = () => {
    setIsVisible(!isVisible);

  };

  return (
    <span onClick={handleClick} className="icon">
       <div>
       <FontAwesomeIcon icon={faChevronUp} onClick={handleClick}></FontAwesomeIcon>
       <FontAwesomeIcon icon={faChevronDown}></FontAwesomeIcon>{isVisible && <span className='TextStyle'>{Text}</span>}

     </div>
    </span>
   );
}

export default ChevronUpDown;
