import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronUp, faChevronDown } from '@fortawesome/free-solid-svg-icons';
import '../Styles/index_module.css'

function ChevronUpDown() {
  const [isVisible, setIsVisible] = useState(true);
  const Text = "Les annonces postées sur Kasa garantissent une fiabilité totale. Les photos sont conformes aux logements, et toutes les informations sont régulièrement vérifiées par nos équipes.";

  const handleClick = () => {
    setIsVisible(!isVisible);

  };

  return (
    <span onClick={handleClick} className="icon"><FontAwesomeIcon icon={isVisible ? faChevronUp : faChevronDown}></FontAwesomeIcon>
      <div>
        <p className="TextStyle">{isVisible && <span className="TextStyle">{Text}</span>}</p>
      </div>
    </span>
   );
}

export default ChevronUpDown;
