import '../Styles/index_Logements_icons.css'
import Logements from '../logements.json'
import { useParams } from 'react-router-dom';

function LocationsIcons() {
    const HandleOnclick = () => {
      const { id } = useParams(id); // Utilisez useParams pour obtenir les paramètres d'URL
      const logement = Logements.find(item => item.id === id);
    console.log(logement);

    };
    console.log(Logements);
    return (
      <div className='Background_icons'>
        {Logements.map((item) => {
          return (
            <div key={item.id}>
              <div style={{ backgroundImage: `url(${item.cover})` }} className='Icons' key={item.url} onClick={HandleOnclick}  >
              <span className='Title'>{item.title} </span> 
              </div>
            </div>
          );
        })}
      </div>
    );
  }

  export default LocationsIcons;
