import Background from '../Assets/Background.png'
import '../Styles/index_Banner_space.css'
import '../Styles/index_ficheLoc'
import Logements from '../logements.json'
import { useParams } from 'react-router-dom';


function FicheLocations ({}) {
  const { id } = useParams(); // Utilisez useParams pour obtenir les paramètres d'URL
  const logement = Logements.find(item => item.id === id);

    if (!logement) {
        return <div>Logement non trouvé.</div>;

    }

    return (
        <div className="BodyBanner">
            <div className="BannerLog">
              <img src={Background} alt="Salon" className="Img_Background"></img>
              <div key={logement.id}>
                  <h2>{logement.title}</h2>
                  <span>{logement.location}</span>
                  <div className="critere_space">
                    <span className="critere"></span>
                    <span className="critere"></span>
                    <span className="critere"></span>
                  </div>
              </div>
            </div>
          </div>
    )
}

export default FicheLocations
