import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronUp, faChevronDown } from '@fortawesome/free-solid-svg-icons';
import styled from 'styled-components';

function ChevronUpDown() {
  const [isVisible, setIsVisible] = useState(true);

  const handleClick = () => {
    setIsVisible(!isVisible);

  const hidden = () =>{styled= hidden}
  };

  return (
    <span onClick={handleClick} className="icon">
       <div>
       <button onClick={handleClick}><FontAwesomeIcon icon={faChevronUp}></FontAwesomeIcon><FontAwesomeIcon icon={faChevronDown}></FontAwesomeIcon></button>
       {isVisible && <p>Texte à afficher et à masquer</p>}
     </div>
    </span>
   );
}

export default ChevronUpDown;
