import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronUp, faChevronDown } from '@fortawesome/free-solid-svg-icons';
import '../Styles/index_module.css'

function ChevronUpDown(props) {
  const [isVisible, setIsVisible] = useState(true);
  const Text = props.text // const text=props
  const Title = props.title;

  const handleClick = () => {
    setIsVisible(!isVisible);

  };

  return (
    <div className='title'>{Title}
      <span onClick={handleClick}><FontAwesomeIcon icon={isVisible ? faChevronUp : faChevronDown}></FontAwesomeIcon>
      <p className="TextStyle">{isVisible && <span className="TextStyle">{Text}</span>}</p>
      </span>
    </div>
   );
}

export default ChevronUpDown;
