import Background from '../Assets/Background.png';
import '../Styles/index_Banner_space.css';
import '../Styles/index_FicheLoc.css';
import Logements from '../logements.json';
import '../Styles/index_carrousel.css';
import { useParams } from 'react-router-dom';
import { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronUp, faChevronDown } from '@fortawesome/free-solid-svg-icons';
import { faChevronRight, faChevronLeft } from '@fortawesome/free-solid-svg-icons';
import Carrousel from './Carrousel';


function FicheLocations() {

  const { id } = useParams();
  const logement = Logements.find(item => item.id === id);

  const [isCollapse, setIsCollapse] = useState(false);
  const [isEquipment, setIsEquipment] = useState(false);

  const toggleCollapse = () => {
    setIsCollapse(!isCollapse)
  };

  const toggleEquipment = () => {
    setIsEquipment(!isEquipment)
  };


  if (!logement) {
    return <div>Logement non trouvé.</div>;
  }                   
  return (
    <div className="BodyBanner">
      <div className="BannerLog">
        <div className="BannerSpace">
          <FontAwesomeIcon icon={faChevronLeft}className="fa left" />              
          <img src={Background} alt="Salon" className="Img_Background" />
          <Carrousel images={logement.pictures} />
          <FontAwesomeIcon icon={faChevronRight} className="fa right" />
        </div>

        <div className="infos_logement_space">
          <div key={logement.id}>          
            <div className="localization_space">
              <div className='location_info'>
                <span className="title_space">
                  <h2 className="">{logement.title}</h2>                                                                                                                                                                                                                                                                                            
                </span>
                <span className="title_log">{logement.location}</span>
                <div className="critere_space">
                {logement.tags.map((tag, index) => (
                  <p key={index} className="critere">
                    {tag.trim()}
                  </p>
                ))}
                </div>
            </div>

            <div className="modules-spaces">

                  <div className="avatar_space">
                    <span className="avatar_name">{logement.host.name}</span>
                    <img
                    className="avatar-bubble avatar"
                    src={logement.host.picture}
                    alt="Host Avatar"
                    />
                    </div>

                    <div className='star_space'>
                  {[...Array(5)].map((_, index) => {
                    let starClass = 'star';
                    if (index < parseInt(logement.rating, 10)) {
                      starClass += ' filled';
                    } else {
                      starClass += ' empty';
                    }
                    return (
                      <span key={index} className={starClass}>
                        &#9733;
                      </span>
                    );
                  })}
                  </div>
            </div>
          </div>

            <div className="description_banner">
              <div className="bubble">
                <span className="character">
                  <FontAwesomeIcon
                    icon={isCollapse ? faChevronDown : faChevronUp}
                    onClick={toggleCollapse}
                    className="Chevron"
                  />
                  Description
                </span>
                {isCollapse && (
                  <div className="ToggleSpace">
                    <p className="ToggleStyle">{logement.description}</p>
                  </div>
                )}
              </div>

              <div className="bubble">
                <span className="character">
                  <FontAwesomeIcon
                    icon={isEquipment ? faChevronDown : faChevronUp}
                    onClick={toggleEquipment}
                    className="Chevron"
                  />
                  Equipements
                </span>
                {isEquipment && (
                  <div className="ToggleSpace">
                    <div className="ToggleStyle">
                      {logement.equipments.map((equipment, index) => (
                        <div key={index}>{equipment}</div>
                      ))}
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default FicheLocations;
