import Background from '../Assets/Background.png';
import '../Styles/index_Banner_space.css';
import '../Styles/index_FicheLoc.css';
import Logements from '../logements.json';
import { useParams } from 'react-router-dom';
import { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronUp, faChevronDown } from '@fortawesome/free-solid-svg-icons';


function FicheLocations() {
  const { id } = useParams();
  const logement = Logements.find(item => item.id === id);

  const [isCollapse, setIsCollapse] = useState(false);
  const [isEquipment, setIsEquipment] = useState(false);

  const toggleCollapse = () => {
    setIsCollapse(!isCollapse)
  };

  const toggleEquipment = () => {
    setIsEquipment(!isEquipment)
  };


  if (!logement) {
    return <div>Logement non trouv√©.</div>;
  }

  return (
    <div className="BodyBanner">
      <div className="BannerLog">
        <img src={Background} alt="Salon" className="Img_Background" />
        <div className="infos_logement_space">
          <div key={logement.id}>
            <div className='title_space'>
              <h2 className=''>{logement.title}</h2>
              <div className='title_space'>
                <span className='title_space_items'>{logement.host.name}</span>
                <img className='avatar-bubble avatar' src={logement.host.picture} alt='Host Avatar' />
              </div>
            </div>
            <div className='modules-spaces'>
              <span className='title_log'>{logement.location}</span>

            </div>
          </div>
          <div className="critere_space">
            {logement.tags.map((tag, index) => (
              <p key={index} className="critere">{tag.trim()}</p>
            ))}
            <div className='rating-container'>
              {[...Array(5)].map((_, index) => {
                let starClass = 'star';
                if (index < parseInt(logement.rating, 10)) {
                  starClass += ' filled';
                } else {
                  starClass += ' empty';
                }

                return (
                  <span key={index} className={starClass}>
                    &#9733;
                  </span>
                );
              })}
            </div>
          </div>

          <div className='description_banner'>
          <div className={`bubble ${isCollapse ? 'expanded' : ''}`}>
              <span className='character'><FontAwesomeIcon icon={isCollapse ? faChevronDown : faChevronUp}
                onClick={toggleCollapse} className='Chevron' />Description
              </span>
              {isCollapse && (
                <div>
                  <p className="ToggleStyle"> {logement.description}</p>
                </div>
              )}
            </div>

            <div className={`bubble ${isEquipment ? 'expanded' : ''}`}>
              <span className='character'><FontAwesomeIcon icon={isEquipment ? faChevronDown : faChevronUp}
                onClick={toggleEquipment} className='Chevron' />Equipements
              </span>
              {isEquipment && (
                <div>
                  <p className="ToggleStyle">{logement.equipments}</p>
                </div>
              )}
            </div>
          </div>

        </div>
      </div>
    </div>
  );
}

export default FicheLocations;
