import Background from '../Assets/Background.png';
import '../Styles/index_Banner_space.css';
import '../Styles/index_FicheLoc.css';
import Logements from '../logements.json';
import { useParams } from 'react-router-dom';
import { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronUp, faChevronDown } from '@fortawesome/free-solid-svg-icons';


function FicheLocations() {
  const { id } = useParams();
  const logement = Logements.find(item => item.id === id);

  const [isCollapse, setIsCollapse] = useState(false);

  const toggleCollapse = () => {
    setIsCollapse(!isCollapse)
  }; 

  if (!logement) {
    return <div>Logement non trouv√©.</div>;
  }

  return (
    <div className="BodyBanner">
      <div className="BannerLog">
        <img src={Background} alt="Salon" className="Img_Background" />
        <div className="infos_logement_space">
          <div key={logement.id}>
            <div className='title_space'>
              <h2 className=''>{logement.title}</h2>
              <div className='title_space'>
                <span className='title_space_items'>{logement.host.name}</span>
                <span className='avatar-bubble avatar'></span>
              </div>
            </div>
            <span className='title_log'>{logement.location}</span>
          </div>
          <div className="critere_space">
            {logement.tags.map((tag, index) => (
              <p key={index} className="critere">{tag.trim()}</p>
            ))}
          </div>
          <div className='description_banner'>
              <div className='bubble'>
                <span className='character'><FontAwesomeIcon icon={isCollapse ? faChevronUp : faChevronDown} onClick={toggleCollapse} className='Chevron'/>Description</span>
