import {faChevronDown} from '@fortawesome/free-solid-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import '..//Styles/index_Apropos.css';
import React, {useState} from 'react';

function ChevronUpDown(){

    const [estVisible, setEstVisible] = useState(true);
    const handleClick = () => {
    setEstVisible(!estVisible)

    };
    return (
        <div>
            <span onClick={handleClick}>
            {estVisible ? (
            <FontAwesomeIcon icon={faChevronUp} className='icon'>

            </FontAwesomeIcon>
            ) : (
                <FontAwesomeIcon icon={faChevronDown} className='icon'></FontAwesomeIcon>
                )}
            </span>
            {estVisible && < span>{{visibility:hidden}}</span> }
        </div>
     );
}


export default ChevronUpDown
